import React, { useState, useEffect } from "react";
import { Link, useLocation } from "react-router-dom";
import { DashboardIcon, FileIcon, SendIcon } from "../components/icons";

interface ILink {
  icon: any;
  title: string;
  url: string;
  submenus?: ILink[];
  setOpened?: any;
}

const ExpendableLink: React.FC<ILink> = ({
  icon,
  title,
  url,
  submenus = [],
  setOpened = () => null,
}) => {
  const location = useLocation();

  const [isOpened, setIsOpened] = useState(false);

  useEffect(() => {
    const submenuIsOpened = submenus
      ?.map((menu) => menu.url)
      .includes(location.pathname);

    if (submenuIsOpened) {
      setIsOpened(true);
      setOpened(true);
    }
  }, []);

  return (
    <li>
      <div
        className={`flex items-center pl-3 py-3 pr-4 ${
          location.pathname == url
            ? "text-gray-50 bg-indigo-500 "
            : "text-gray-50 hover:bg-gray-900"
        } 
        
        ${isOpened ? "bg-gray-900" : ""} rounded`}
      >
        <span className="inline-block mr-3 ">{icon}</span>
        <Link to={url}>
          <span> {title} </span>
        </Link>

        {submenus?.length > 0 && (
          <span
            className={`inline-block ml-auto transition-all ${
              isOpened ? "" : " -rotate-90"
            }`}
            onClick={() => setIsOpened((prev) => !prev)}
          >
            <svg
              className="text-gray-400 w-3 h-3"
              viewBox="0 0 10 6"
              fill="none"
              xmlns="http://www.w3.org/2000/svg"
            >
              <path
                d="M9.08329 0.666626C8.74996 0.333293 8.24996 0.333293 7.91663 0.666626L4.99996 3.58329L2.08329 0.666626C1.74996 0.333293 1.24996 0.333293 0.916626 0.666626C0.583293 0.999959 0.583293 1.49996 0.916626 1.83329L4.41663 5.33329C4.58329 5.49996 4.74996 5.58329 4.99996 5.58329C5.24996 5.58329 5.41663 5.49996 5.58329 5.33329L9.08329 1.83329C9.41663 1.49996 9.41663 0.999959 9.08329 0.666626Z"
                fill="currentColor"
              ></path>
            </svg>
          </span>
        )}
      </div>
      <div className={` ${isOpened ? "" : "hidden "} pl-3 my-2 `}>
        {submenus?.map((menu) => (
          <ExpendableLink
            setOpened={setIsOpened}
            icon={menu.icon}
            title={menu.title}
            url={menu.url}
            submenus={menu.submenus}
          />
        ))}
      </div>
    </li>
  );
};

